let main = document.getElementById("main");
let text = document.getElementById("text");

function colors() {
    let color_bec = Math.round(Math.random() * 20000)
    let hex_bec = "#" + color_bec
    main.style.background = hex_bec
    let text_color = Math.round(Math.random() * 10000)
    let text_hex = "#" + text_color
    text.style.color = text_hex


}
setInterval(() => {
    colors()

}, 1000)